import { ComponentContext } from '@teambit/generator';

export const serverRootFile = (context: ComponentContext) => {
  const { name, namePascalCase: Name } = context;
  return {
    relativePath: `server.app-root.tsx`,
    content: `import ReactDOMServer from "react-dom/server";
import { StaticRouter } from "react-router-dom/server";
import { ${Name} } from "./${name}";

interface IRenderProps {
  path: string;
}
    
export const render = async ({ path }: IRenderProps) => {
  return ReactDOMServer.renderToString(
    <StaticRouter location={path}>
      <${Name} />
    </StaticRouter>
  );
};
    
/**
 * implement loadScripts() to inject scripts to the head
 * during SSR.
 */
// export const loadScripts = async () => {
//   return '<script></script>';
// }
`,
  };
};
