/* THIS EXAMPLE DEMONSTRATES HOW TO STYLE A COMPONENT WITH CSS TO MAKE IT THEMEABLE */

import React from 'react';

/* style this button using CSS props */
export const Button = () => {
  /*
   * these css props were generated by the `createTheme` funciton. They are made available in the page by the generated Theme component.
   */
  const myStyle = {
    backgroundColor: 'var(--color-background)',
    color: 'var(--color-text)',
  };
  return <button style={myStyle}>A Button Styled using CSS Props</button>;
};
