import React from 'react';
import { Theme } from './examples/default-theme.demo';
import { Button as ButtonStyledWithCss } from './examples/button-styled-with-css.demo';
import { Button as ButtonStyledWithJs } from './examples/button-styled-with-js.demo';
import { darkThemeOverrides } from './examples/dark-theme.demo';

const Space = () => <div style={{ marginBottom: 25 }} />;

/* uses a theme that was generated by the theme-provider */
export const ButtonsThemedByTheDefaultTheme = () => {
  return (
    <Theme.ThemeProvider>
      <ButtonStyledWithJs />
      <Space />
      <ButtonStyledWithCss />
    </Theme.ThemeProvider>
  );
};

/* overrides the default theme with the dark-theme design token values */
export const ButtonsThemedByTheDarkTheme = () => {
  return (
    <Theme.ThemeProvider overrides={darkThemeOverrides}>
      <ButtonStyledWithJs />
      <Space />
      <ButtonStyledWithCss />
    </Theme.ThemeProvider>
  );
};
